import React, { useState } from 'react';
import './LoginSignup.css';

const LoginSignup = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handlePasswordChange = (e) => {
    setPassword(e.target.value);
  };

  const handleFirstNameChange = (e) => {
    setFirstName(e.target.value);
  };

  const handleLastNameChange = (e) => {
    setLastName(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (isLogin) {
      // TODO: Implement login logic using email and password
    } else {
      // TODO: Implement signup logic using email, password, firstName, and lastName
    }
  };

  return (
    <div className="container">
      <h2 className="title">{isLogin ? 'Log In' : 'Sign Up'}</h2>
      <form className="form" onSubmit={handleSubmit}>
        {!isLogin && (
          <>
            <label>
              First Name:
              <input type="text" value={firstName} onChange={handleFirstNameChange} required />
            </label>
            <br />
            <label>
              Last Name:
              <input type="text" value={lastName} onChange={handleLastNameChange} required />
            </label>
            <br />
          </>
        )}
        <label>
          Email:
          <input type="email" value={email} onChange={handleEmailChange} required />
        </label>
        <br />
        <label>
          Password:
          <input type="password" value={password} onChange={handlePasswordChange} required />
        </label>
        <br />
        <button type="submit" className="submit-button">
          {isLogin ? 'Login' : 'Signup'}
        </button>
      </form>
      <p className="switch-mode">
        {isLogin ? "Don't have an account?" : 'Already have an account?'}
        <button onClick={() => setIsLogin(!isLogin)} className="switch-button">
          {isLogin ? 'Sign Up' : 'Log In'}
        </button>
      </p>
    </div>
  );
};

export default LoginSignup;
